import React from "https://esm.sh/react";
import ReactDOM from "https://esm.sh/react-dom";

function Fantominus() {
  const canvasRef = React.useRef(null);
  React.useEffect(() => {
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth / window.innerHeight,
      0.1,
      1000
    );
    const renderer = new THREE.WebGLRenderer({ alpha: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setClearColor(0x000000, 0);
    canvasRef.current.appendChild(renderer.domElement);

    const circleGeometry = new THREE.CircleGeometry(0.6, 32);
    const circleMaterial = new THREE.MeshBasicMaterial({
      color: 0x000000,
    });

    const loader = new THREE.TextureLoader();
    const smokeTexture = loader.load(
      "data:image/png;base64,"
    );

    const smokeParticles = [];
    const smokeMaterial = new THREE.SpriteMaterial({
      map: smokeTexture,
      transparent: true,
      opacity: 0.4,
      color: 0x796186,
      depthWrite: false,
      depthTest: false,
    });
    const maxDistance = 0.6 * 2;

    for (let i = 0; i < 50; i++) {
      const particle = new THREE.Sprite(smokeMaterial);
      particle.position.set(
        (Math.random() - 0.5) * 1,
        (Math.random() - 0.5) * 1,
        -5.1
      );
      particle.scale.set(1.5, 1.5, 1);
      particle.userData = {
        velocityX: (Math.random() - 0.5) * 0.01,
        velocityY: (Math.random() - 0.5) * 0.01,
        scaleSpeed: Math.random() * 0.002 + 0.001,
      };
      scene.add(particle);
      smokeParticles.push(particle);
    }

    camera.position.z = 5;

    function animate() {
      requestAnimationFrame(animate);

      smokeParticles.forEach((particle) => {
        particle.position.x += particle.userData.velocityX;
        particle.position.y += particle.userData.velocityY;
        particle.rotation.z += 0.01;
        particle.scale.x += particle.userData.scaleSpeed;
        particle.scale.y += particle.userData.scaleSpeed;

        const distanceFromCenter = Math.sqrt(
          particle.position.x ** 2 + particle.position.y ** 2
        );

        if (distanceFromCenter > maxDistance) {
          particle.position.set(
            (Math.random() - 0.5) * 1,
            (Math.random() - 0.5) * 1,
            -5
          );
          particle.material.opacity = 0.8;
          particle.scale.set(1.5, 1.5, 1);
        }
      });

      renderer.render(scene, camera);
    }

    animate();

    return () => {
      renderer.dispose();
    };
  }, []);
  return (
    <div className="Fantominus">
      <div ref={canvasRef}></div>
      <ul>
        <li></li>
      </ul>
    </div>
  );
}

